import { ReadVarExpr } from '@angular/compiler';
import { Component, CUSTOM_ELEMENTS_SCHEMA, OnInit } from '@angular/core';
import { async } from '@angular/core/testing';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { NativeDateAdapter } from '@angular/material/core';
import { DomSanitizer, Title } from '@angular/platform-browser';
import { newProducto, EquipoService, uploadPhoto } from "../../SERVICES/equipo.service";




interface HtmlInputEvent extends Event {
  target: HTMLInputElement & EventTarget | null;
}

@Component({
  selector: 'new-products',
  templateUrl: './new-products.component.html',
  styleUrls: ['./new-products.component.css']
})
export class NewProductsComponent implements OnInit {
  public previsualizacion:any;
  public archivos: any=[] //Sera de tipo array
  public image: any //Enviar una imagen a la vez al servidor

  constructor(private equipoService:EquipoService, private sanitizer: DomSanitizer) {
   }

  ngOnInit(): void {
    this.producto.fk_id_user=localStorage.getItem('token')
  }

//agregar el formGrup
productoForm=new FormGroup({
  // nombre: new FormControl('',[Validators.required, Validators.minLength(2) ]),
  titulo: new FormControl('',[Validators.required,
  Validators.minLength(3),
  Validators.maxLength(20)]),
  precio: new FormControl('',[Validators.required,
  Validators.minLength(2)]),
  categoria:new FormControl('',[Validators.required]),
  estado:new FormControl('',[Validators.required]),
  decripcion:new FormControl('',[Validators.required,
  Validators.minLength(3), Validators.maxLength(30)]),
  ubicacion:new FormControl('',[Validators.required])
})
/*
get nombreControl():FormControl{
  return this.loginForm.get('nombre') as FormControl
}*/

get tituloControl():FormControl{
  return this.productoForm.get('titulo') as FormControl
}
get precioControl():FormControl{
  return this.productoForm.get('precio') as FormControl
}
get categoriaControl():FormControl{
  return this.productoForm.get('categoria') as FormControl
}
get estadoControl():FormControl{
  return this.productoForm.get('estado') as FormControl
}
get descripcionControl():FormControl{
  return this.productoForm.get('decripcion') as FormControl
}
get ubicacionControl():FormControl{
  return this.productoForm.get('ubicacion') as FormControl
}

capturarFile(event:any): any{
  if(event.target.files.length < 10){
      Array.from(event.target.files).forEach((element:any) => {
        this.loadFile(element);
          this.archivos.push(element)
          console.log("\n\n<<<<"+this.archivos.length+"\n\n");
      });
}else{
  alert('No más de 10 imágenes')
}
}

  loadFile(file:File){
    const reader=new FileReader();
    reader.onload=()=>{
      console.log(reader.result)

    this.previsualizacion=reader.result

    //this.archivos.push(reader.result)

    };
    reader.readAsDataURL(file);

  }



  //Extraer el base 64
  /*
  extraerBase64 =async ($event:any) => new Promise((resolve,reject) =>{
    try{
      const unsafeImg = window.URL.createObjectURL($event);
      const image= this.sanitizer.bypassSecurityTrustUrl(unsafeImg);
      const reader = new FileReader();

      reader.readAsDataURL($event);
      reader.onload =() =>{
        resolve({
          base:reader.result
        });
      };
      reader.onerror = error =>{
        resolve({
          base:null
        });
      }; return null;
    }catch(e){
      return null;
    }
  });
  */




  producto:newProducto={
    fk_id_user: '',
    fk_id_department: '',
    fk_id_product_category:'',
    fk_id_product_status: '',
    var_name: '',
    text_description: '',
    dou_price:0
  }

  /*agregarFavorito(){
    //console.log(this.dataEntrante);
    /*la vraible de servicio con atributo de servicio emite un atributo =emit */
    /*this.servicefavorito.disparadordeFavoritos.emit({
      data:this.dataEntrante
    })
  } */

  /* Para subir Archivo*/
  subirArchivo():any{

    //Sube el producto
    this.equipoService.newProducto(this.producto)
    .subscribe(res=>{

      var info:BookInfo = <any>res

      //Recorre el arreglo de archivos
      this.archivos.forEach((archivo:any) => {
        const formularioDeDatos = new FormData();
        formularioDeDatos.append('image',archivo)
        console.log(archivo)

        //Sube archivo uno por uno
        this.equipoService.productoFoto(formularioDeDatos, info.id)
          .subscribe(res=>{
            console.log('Respuesta ',res)
        })

      })

      console.log('respuesta del servidor',res);

    })


    try{
    }catch(e){
      console.log('Error',e);
    }
  }
}

interface BookInfo {
  status : string ;
  id: string;
}
<head>
    <link rel="stylesheet" href="new-products.component.css">
    <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/css/bootstrap.min.css" integrity="sha384-1BmE4kWBq78iYhFldvKuhfTAU6auU8tT94WrHftjDbrCEXSU1oBoqyl2QvZ6jIW3" crossorigin="anonymous">
</head>

<label>




    <!-- Button trigger modal -->
<button type="button" class="btn btn-primary" data-bs-toggle="modal" data-bs-target="#myModal">
    Agregar Producto
  </button>

  <!-- Modal -->
  <div class="modal" id="myModal">
    <div class="modal-dialog">
      <div class="modal-content">
        <!-- Modal Header -->
        <div class="modal-header text-center">
          <h5  class="modal-title w-100">Articulo en Venta </h5>
        </div>
        <!-- Modal Body -->
        <div class="modal-body">
            <br>


            <form class="div_padre" [formGroup]="productoForm">

                <div class="div_padre" >
                    <div class="form-group">
                        <label for="imagenn">
                          <div style="display:inline-block;vertical-align:top;">
                            <img title="imagen" src="../../../assets/Imagenes/fotoImagenes.jfif" style = "cursor: pointer;">
                            </div>
                            <div style="display:inline-block;">
                              <p>
                                La foto es requerida(Máximo 10 imágenes).
                              </p>
                            </div>
                        </label>
                        <input id="imagenn" style = "display: none; visibility: none;" class="form-control-file" (change)="capturarFile($event)" type="file" placeholder="seleccciona" multiple>                      </div >
                    <br>


                    <!--Previsualizacion-->
                    <!-- <div *ngIf="previsualizacion">
                        <img [src]="previsualizacion" alt="">
                    </div>  -->
                    <br>
                    <div *ngFor="let item of archivos" class="contenedor-imagenes">
                        <img [src]="item"  alt="" width="30" class="imag" >
                    </div>

                </div>
                <br>
                <!--Boton para subir el archivo-->
                <div class="form">
                    <div class="form-floating mb-3 mt-3">
                      <input type="text" id="titulo" class="form-control form-control-lg" [(ngModel)]="producto.var_name" placeholder="Escribe el titulo" formControlName="titulo" name="titulo" #titulo required
                      formControlName="titulo">
                      <label for="titulo">Título</label>
                    </div>
                    <div class="errors-container" *ngIf="tituloControl.invalid && (tituloControl.touched || tituloControl.dirty)">
                      <p class="errors-msg" class="fs-6" text *ngIf="tituloControl.hasError('required')">El campo es obligatorio</p>
                      <p class="errors-msg" class="fs-6" *ngIf="tituloControl.hasError('minlength')">Nombre es muy corto</p>
                      <p class="errors-msg" class="fs-6" *ngIf="tituloControl.hasError('maxLength')">Nombre es muy largo</p>

                  </div>

                </div>

                <div class="form">
                    <div class="form-floating mb-3 mt-3">
                      <input type="text" id="precio" class="form-control form-control-lg" [(ngModel)]="producto.dou_price" placeholder="precio" #precio required formControlName="precio">
                      <label for="precio">Precio</label>
                    </div>
                    <div class="errors-container" *ngIf="precioControl.invalid && (precioControl.touched || precioControl.dirty)">
                      <p class="errors-msg" *ngIf="precioControl.hasError('required')">El campo es obligatorio</p>


                  </div>
                </div>


                <div class="form">
                    <div class="form-floating mb-3 mt-3">
                    <select class="form-select" [(ngModel)]="producto.fk_id_product_category"  id="categoria" name="categoria" required formControlName="categoria">
                            <option value="1">Hogar</option>
                            <option value="2">Futuros Padres</option>
                            <option value="3">Mascotas</option>
                            <option value="4">Inmuebles</option>
                            <option value="5">Hogar</option>
                            <option value="6">Vehiculos</option>
                            <option value="7">Moda</option>
                            <option value="8">Electrónica</option>
                            <option value="9">Servicios</option>
                            <option value="10">Negocios</option>
                            <option value="11">Hogar</option>
                            <option value="12">Empleo</option>

                    </select>
                    <label class="nom" for="categoria">Categoria de Producto</label>
                    </div>
                    <div class="errors-container" *ngIf="categoriaControl.invalid && (categoriaControl.touched || categoriaControl.dirty)">
                      <p class="errors-msg" class="fs-6" *ngIf="categoriaControl.hasError('required')">El campo es obligatorio selecciona una opcion</p>

                  </div>
                </div>




                <div  class="form">
                    <div class="form-floating mb-3 mt-3">
                        <select class="form-select" [(ngModel)]="producto.fk_id_product_status" name="estado" id="estado"  formControlName="estado">
                        <option value="1">Nuevo</option>
                        <option value="2">Usado</option>
                      </select>
                      <label for="Estado">Estado del producto</label>
                    </div>
                    <div class="errors-container" *ngIf="estadoControl.invalid && (estadoControl.touched || estadoControl.dirty)">
                      <p class="errors-msg" class="fs-6" *ngIf="estadoControl.hasError('required')">El campo es obligatorio</p>

                  </div>
                </div>

                <div class="form">
                    <div class="form-floating mb-3 mt-3">
                      <textarea class="form-control form-control-lg" id="descripcion" [(ngModel)]="producto.text_description" rows="2"  placeholder="descripcion" required  formControlName="decripcion"></textarea>
                      <label for="descripcion">Descripcion del Producto</label>
                    </div>
                    <div class="errors-container" *ngIf="descripcionControl.invalid && (descripcionControl.touched || descripcionControl.dirty)">
                      <p class="errors-msg" class="fs-6" *ngIf="descripcionControl.hasError('required')">El campo es obligatorio</p>
                      <p class="errors-msg" class="fs-6" *ngIf="descripcionControl.hasError('minlength')">La descripcion es muy corta</p>
                      <p class="errors-msg" class="fs-6" *ngIf="descripcionControl.hasError('maxLength')">La descripcion es muy largo</p>

                  </div>
                </div>

                <div class="form">
                    <table class="table">
                        <thead class="table" >
                            <tr>
                              <div>
                                <td class="tbl-col-nombre">Ubicación</td>
                                <td class="tbl-col-depto">
                                    <select class="form-select" [(ngModel)]="producto.fk_id_department"
                                     name="Departamento" formControlName="ubicacion" id="ubicacion">
                                        <option value="1">Atlantida</option>
                                        <option value="2">Choluteca</option>
                                        <option value="3">Colon</option>
                                        <option value="4">Comayagua</option>
                                        <option value="5">Copan</option>
                                        <option value="6">Cortes</option>
                                        <option value="7">El Paraiso</option>
                                        <option value="8">Francisco Morazán</option>
                                        <option value="9">Gracias a Dios</option>
                                        <option value="10">Intibuca</option>
                                        <option value="11">Islas de la Bahia</option>
                                        <option value="12">La Paz</option>
                                        <option value="13">Lempira</option>
                                        <option value="14">Ocotepeque</option>
                                        <option value="15">Olancho</option>
                                        <option value="16">Santa Barbara</option>
                                        <option value="17">Valle</option>
                                        <option value="18">Yoro</option>
                                    </select>
                                  <div class="errors-container" *ngIf="ubicacionControl.invalid && (ubicacionControl.touched || ubicacionControl.dirty)">
                                    <p class="errors-msg" class="fs-6" *ngIf="ubicacionControl.hasError('required')">El campo es obligatorio selecciona una opcion</p>

                                </div>
                                </td>
                                </div>
                            </tr>
                        </thead>
                    </table>
                </div>




            </form>







        </div>
        <div class="modal-footer">
              <!--Boton para subir el archivo y cerrar el modal-->
          <button type="button" onclick="document.getElementById('titulo').value = ''; document.getElementById('precio').value = '';
          document.getElementById('categoria').value='';document.getElementById('categoria').value='';document.getElementById('estado').value='';
          document.getElementById('descripcion').value='';document.getElementById('ubicacion').value=''"
          class="btn btn-secondary" data-bs-dismiss="modal">Cerrar</button>
          <button type="button" class="btn btn-primary" data-bs-dismiss="modal" type="submit" (click)="subirArchivo()"
          onclick="document.getElementById('titulo').value = ''; document.getElementById('precio').value = '';
          document.getElementById('categoria').value='';document.getElementById('categoria').value='';document.getElementById('estado').value='';
          document.getElementById('descripcion').value='';document.getElementById('ubicacion').value=''"
          >Guardar</button>
        </div>
      </div>
    </div>
  </div>







</label>
